mapper.select:


    a.del_flag = '0'
    and a.apply_id = #{applyId}
    and a.applicant_id = #{applicantId}
    and a.start_time >= #{startTime}
    and a.end_time <= #{endTime}

        and a.apply_status in

            #{applyStatus}



order by a.apply_status asc, a.create_time desc



    a.del_flag = '0'
    and a.apply_id = #{applyId}
    and a.applicant_id = #{applicantId}
    and a.room_id = #{roomId}
    and a.dept_id = #{deptId}
    and a.apply_type = #{applyType}
    and a.apply_reason = #{applyReason}
    and a.start_time >= #{startTime}
    and a.end_time <= #{endTime}
    and a.apply_status = #{applyStatus}
    and a.create_by = #{createBy}
    and a.create_time = #{createTime}
    and a.update_by = #{updateBy}
    and a.update_time = #{updateTime}
    and a.del_flag = #{delFlag}
    and a.remark = #{remark}

order by a.apply_status,a.create_time desc

select a.room_id as 'roomId', u.sex as applyUserSex, count(1) as 'applyNumber' from umap_apply_info a left join sys_user u on a.applicant_id = u.user_id
a.del_flag = '0' and a.apply_status in (0, 1) and a.start_time = #{startTime}
group by a.room_id, u.sex



    a.del_flag = '0'
    and a.start_time >= #{startTime}
    and a.end_time <= #{endTime}

order by a.apply_status,a.create_time desc

mapper.update:
update umap_apply_info
applicant_id = #{applicantId}, room_id = #{roomId}, dept_id = #{deptId}, apply_type = #{applyType}, apply_reason = #{applyReason}, start_time = #{startTime}, end_time = #{endTime}, apply_status = #{applyStatus}, approve_status = #{approveStatus}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag}, remark = #{remark},
where apply_id = #{applyId}

mapper.select:


    and approve_status = #{approveStatus}
    and (user_id = #{userId} or create_by = #{userId})
    and start_time >= #{startTime}
    and end_time <= #{endTime}

order by approve_status asc, create_time desc

SELECT
date_format(m.start_time, '%Y-%m-%d') name,
COUNT(1) count,
'已预约' info
FROM
umap_apply_info m

    and YEAR (m.start_time) = #{year}
    and MONTH (m.start_time) = #{month}
    and m.apply_status = #{applyStatus}
    and m.applicant_id = #{applicantId} 

GROUP BY TO_DAYS(m.start_time)
order by name



    and DATE_FORMAT(start_time,"%Y-%m-%d") = #{checkDate} 
    and a.apply_id = #{applyId} 
    and a.approve_status = #{approveStatus} 
    and a.apply_status = #{applyStatus} 
    and a.applicant_id = #{applicantId} 
    and a.del_flag = '0'

order by a.apply_status,a.create_time desc
