SELECT count(a.room_id) as 'value' ,r.`name` as 'name' FROM umap_arraign_appointment as a
left join umap_arraign_room as r on a.room_id = r.id

     and start_time >= #{startTime}
     and end_time <= #{endTime}
    and a.criminal_name like CONCAT('%',#{criminalName},'%')
    and a.criminal_type = #{criminalType}
    and a.criminal_birth = #{criminalBirth}
    and a.stage = #{stage}
    and a.criminal_admit = #{criminalAdmit}
    and a.need_legal_aid = #{needLegalAid}
    and a.prosecutor_id like CONCAT('%',#{prosecutorId},'%') 
    and a.prosecutor_name like CONCAT('%',#{prosecutorName},'%')
    and a.status = #{status}

        and to_days(start_time) = to_days(now())

    and a.create_by = #{createBy}
    and a.create_time = #{createTime}
    and a.update_by = #{updateBy}
    and a.update_time = #{updateTime}
    and a.del_flag = #{delFlag}

        and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= start_time


        and DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= start_time


        and DATE_SUB(CURDATE(), INTERVAL 90 DAY) <= start_time


        and YEAR(start_time)=YEAR(NOW())

    and a.del_flag = 0

group by a.room_id;

