mapper.select:


    and aa.criminal_type = #{criminalType}
    and aa.room_id = #{roomId}
    and aa.status = #{status}
    and TO_DAYS(aa.start_time) = TO_DAYS(#{appointmentDate})

and aa.del_flag = '0' and u.del_flag = '0'

SELECT date_format(a.start_time,'%Y-%m-%d') dayName, COUNT(1) dayCount, '' info FROM umap_arraign_appointment a
and YEAR (a.start_time) = #{year} and MONTH (a.start_time) = #{month} and (a.create_by = #{createBy} or a.prosecutor_user_id = #{prosecutorUserId}) and a.status = #{status}
GROUP BY TO_DAYS(a.start_time) order by dayName

WHERE u.del_flag = '0' and u.user_id in ( select DISTINCT prosecutor_user_id from umap_arraign_appointment
AND (create_by = #{createBy} or prosecutor_user_id = #{prosecutorUserId}) AND TO_DAYS(start_time) = TO_DAYS(#{checkDate}) AND start_time > #{limitDate}
)

